import { useState, useRef, useEffect } from "react";
import { OrbitControls } from "drei";
import { useBox, Physics } from "use-cannon";

// project box
function HistoryItem(props) {
  // This reference will give us direct access to the mesh
  const mesh = useRef();
  // Set up state for the hovered and active state
  const [hovered, setHover] = useState(false);
  const projSelected = props.canvasProjects.includes(props.project);

  const handleSphereClicked = () => {
    if (!projSelected) {
      props.setCanvasProjects([...props.canvasProjects, props.project]);
    }
  };

  const handlePointerIn = () => {
    setHover(true);
    props.setHoveredProject(props.project);
  };

  const handlePointerOut = () => {
    setHover(false);
    props.setHoveredProject(undefined);
  };

  let dim = 0.05;

  let xyz_proj = props.project["x_y_z_pos"][0];
  let x_project = xyz_proj[0];
  let y_project = xyz_proj[1];
  let z_project = xyz_proj[2];

  let opacity = 0.3;
  let color = "gray";
  if (projSelected) {
    color = "blue";
    opacity = 0.8;
  }
  if (props.project.description === "User uploaded title.") {
    color = "darkgreen";
    opacity = 0.8;
  }

  if (props.isSynth) {
    color = "orange";
    opacity = 0.8;
  }
  if (hovered | (props.project === props.hoveredProject)) {
    color = "purple";
    opacity = 0.8;
  }

  return (
    <mesh
      ref={mesh}
      onClick={(event) => handleSphereClicked()}
      onPointerOver={(event) => handlePointerIn()}
      onPointerOut={(event) => handlePointerOut()}
      position={[x_project, y_project, z_project]}
    >
      <sphereGeometry args={[dim, dim, dim]} />
      <meshStandardMaterial
        color={color}
        transparent={true}
        opacity={opacity}
      />
    </mesh>
  );
}

export default HistoryItem;
