import { useState, useRef, useEffect } from "react";
// import { Billboard } from "drei";

// item box
function HistoryItem(props) {
  const SCALE = 0.5;

  const k = 30;
  function sigmoid(z) {
    return 1 / (1 + Math.exp(-z / k));
  }
  // This reference will give us direct access to the mesh
  const mesh = useRef();
  // Set up state for the hovered and active state
  const [hovered, setHover] = useState(false);
  const itemSelected = props.canvasItems.includes(props.item);

  const handleSphereClicked = () => {
    if (!itemSelected) {
      props.setCanvasItems([...props.canvasItems, props.item]);
    }
  };

  const handlePointerIn = () => {
    setHover(true);
    props.setHoveredItem(props.item);
  };

  const handlePointerOut = () => {
    setHover(false);
    props.setHoveredItem(undefined);
  };

  let dim = sigmoid(props.item["visitCount"]) * 1.5;

  let x_item = props.item["3d_x"] / SCALE;
  let y_item = props.item["3d_y"] / SCALE;
  let z_item = props.item["3d_z"] / SCALE;

  let opacity = 0.3;
  let color = "gray";
  if (itemSelected) {
    color = "blue";
    opacity = 0.8;
  }

  if (hovered | (props.item === props.HoveredItem)) {
    color = "purple";
    opacity = 0.8;
  }

  return (
    <mesh
      ref={mesh}
      onClick={(event) => handleSphereClicked()}
      onPointerOver={(event) => handlePointerIn()}
      onPointerOut={(event) => handlePointerOut()}
      position={[x_item, y_item, z_item]}
    >
      <boxGeometry args={[dim, dim, dim]} />
      <meshStandardMaterial
        color={color}
        transparent={true}
        opacity={opacity}
      />
    </mesh>
  );
}

export default HistoryItem;
